import React,{useState,useEffect} from 'react'
import Tooltip from '@mui/material/Tooltip';
import { Button, Dialog, Grid } from '@material-ui/core'
import { makeStyles } from '@mui/styles';
import { useQuery } from "@apollo/client";
import { LOAD_USERS } from './GraphQL/Queries';
import TextField from '@mui/material/TextField';
import Alert from '@mui/material/Alert';
import Snackbar from '@mui/material/Snackbar';
import DeleteOutlineOutlinedIcon from '@mui/icons-material/DeleteOutlineOutlined';
import EditOutlinedIcon from '@mui/icons-material/EditOutlined';
import FiberManualRecordIcon from '@mui/icons-material/FiberManualRecord';

const useStyles = makeStyles({
  root: {
    "&:hover":{
      cursor:"pointer"
    }
  },
  dialog:{
    minHeight: "40vh",
    padding: 10,
  },
  button:{
    background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    border: 0,
    borderRadius: 3,
    boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    color: 'white !important',
    height: 48,
    padding: '0 30px',
  }
});

const List = ({style,fetching,updateFetching}) => {
    const classes = useStyles();
    const { data , refetch} = useQuery(LOAD_USERS);
    const [editModel,setEditModelOpen] = useState(false)
    const [userIndex,setUserIndex] = useState(null)
    const [alert,setAlert] = useState(false)
    const [users, setUsers] = useState([]);
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')

    useEffect(() => {
        if (data) {
          setUsers(data.getAllUsers);
        }
      }, [data]);

    useEffect(()=>{
      refetch()
      updateFetching(false)
    },[fetching])


    return (
        <div style={style}>
          <div style={{
                display:"flex",
                flexDirection:"column",
                alignItems:"center"
              }}
            >
              <h1 
                style={{
                  color:"#FE6B8B"
                }}>
                Read list of users detail 
              </h1>
              {!!users && users.map((val,index) => {
                  return (
                    <div 
                      style={{
                        display:"flex",
                        flexDirection:"row"
                      }}
                    >
                      <h4 style={{textAlign:"center",position:"relative"}}> 
                        <span 
                          style={{
                            color:"#FE6B8B"
                          }}>
                          <FiberManualRecordIcon/> &nbsp;   
                        </span> 
                          name: {val.name} and email: {val.email}
                      </h4>
                      <div style={{margin:"auto 30px", display:"flex",flexDirection:"row"}}>
                          <Tooltip title="Update">
                            <div className={classes.root} onClick={()=>{
                                  setUserIndex(index)
                                  setEditModelOpen(true)
                                }}>
                                <EditOutlinedIcon/>
                            </div>
                          </Tooltip>
                          <Tooltip title="Delete">
                            <div className={classes.root} onClick={()=>setAlert(true)}>
                                <DeleteOutlineOutlinedIcon/>
                            </div>
                          </Tooltip>
                      </div>
                    </div>
                  )
              })}
          </div>
          {!!editModel && (
            <Grid container direction={"column"} alignItems={"center"}>
              <Dialog
                  classes={{ paper: classes.dialog }}
                  open={editModel}
                  onClose={()=>setEditModelOpen(false)}
                  maxWidth={'md'}
                  fullWidth={true} 
              >
                  <h2 style={{
                    width:"50%",
                    marginLeft:"auto",
                    marginRight:"auto",
                    textAlign:"center",
                    color:"#FE6B8B"
                  }}>
                    Update User {userIndex}
                  </h2>
                  <TextField 
                    style={{
                      marginBottom:"10px",
                      textJustify:"auto",
                      width:"50%",
                      marginLeft:"auto",
                      marginRight:"auto"
                    }}
                    label="Name" 
                    variant="outlined" 
                    value={name}
                    onChange={(e)=>setName(e.target.value)}
                />
                <TextField 
                    label="Email"
                    style={{
                      marginBottom:"10px",
                      width:"50%",
                      marginLeft:"auto",
                      marginRight:"auto"
                    }} 
                    variant="outlined" 
                    value={email}
                    onChange={(e)=>setEmail(e.target.value)}
                />
                <div 
                  style={{
                    display:"flex",
                    flexDirection:"row",
                    justifyContent:"center"
                  }}
                >
                  <Tooltip title="Close">
                    <Button
                      classes={{
                          root:classes.button
                        }}
                      style={{
                        width:"25%",
                        marginRight:"5px"
                      }}
                      onClick={()=>setEditModelOpen(false)}
                    >
                      Close
                    </Button>
                  </Tooltip>
                  <Tooltip title="Update">
                    <Button
                      classes={{
                          root:classes.button
                        }}
                      style={{
                        width:"25%",
                      }}
                    >
                      Update User
                    </Button>
                  </Tooltip>
                </div>
              </Dialog>
            </Grid>
          )}
          {!!alert && (
            <Snackbar open={alert} autoHideDuration={2000} onClose={()=>setAlert(false)}>
              <Alert severity="success" sx={{ width: '100%' }}>
                User record has been deleted
              </Alert>
            </Snackbar>
          )}
        </div>
    )
}

export default List
