import { gql } from "@apollo/client";

export const CREATE_USER_MUTATION = gql`
  mutation createUser(
    $name: String!
    $email: String!
  ) {
    createUser(
      name: $name
      email: $email
    ) {
      name
      email
    }
  }

  mutation uodateUser(
    $id: Int!
    $name: String!
    $email: String!
  ){
    uodateUser(id:$id, name:$name, email:$email){
      id
    }
  }
`;
