import React,{useState,useEffect} from 'react'
import Tooltip from '@mui/material/Tooltip';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import { Button, Dialog, Grid } from '@material-ui/core'
import { makeStyles } from '@mui/styles';
import { useQuery } from "@apollo/client";
import { LOAD_USERS } from './GraphQL/Queries';
import TextField from '@mui/material/TextField';



const useStyles = makeStyles({
  root: {
    "&:hover":{
      cursor:"pointer"
    }
  },
  dialog:{
    minHeight: "40vh",
    padding: 10,
  },
  button:{
    background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    border: 0,
    borderRadius: 3,
    boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    color: 'white !important',
    height: 48,
    padding: '0 30px',
  }
});

const List = ({style}) => {
    const classes = useStyles();
    const { data } = useQuery(LOAD_USERS);
    const [editModel,setEditModelOpen] = useState(false)
    const [users, setUsers] = useState([]);
    useEffect(() => {
        if (data) {
          setUsers(data.getAllUsers);
        }
      }, [data]);
    return (
        <div style={style}>
          <div style={{
                display:"flex",
                flexDirection:"column",
                alignItems:"center"
              }}
            >
              <h1 
                style={{
                  color:"#FE6B8B"
                }}>
                Read list of users detail 
              </h1>
              {users.map((val,index) => {
                  return (
                    <div 
                      style={{
                        display:"flex",
                        flexDirection:"row"
                      }}
                    >
                      <h4> 
                        <span 
                          style={{
                            color:"#FE6B8B"
                          }}>
                          {index}. &nbsp;   
                        </span> 
                          {val.firstName}
                      </h4>
                      <div style={{margin:"auto 30px", display:"flex",flexDirection:"row"}}>
                          <Tooltip title="Update">
                            <div className={classes.root} onClick={()=>setEditModelOpen(true)}>
                                <EditIcon/>
                            </div>
                          </Tooltip>
                          <Tooltip title="Delete">
                            <div className={classes.root} onClick={()=>console.log("delete")}>
                                <DeleteIcon/>
                            </div>
                          </Tooltip>
                      </div>
                    </div>
                  )
              })}
          </div>
          {!!editModel && (
            <Grid container direction={"column"} alignItems={"center"}>
              <Dialog
                  classes={{ paper: classes.dialog }}
                  open={editModel}
                  onClose={()=>setEditModelOpen(false)}
                  maxWidth={'md'} 
                  fullWidth={true} 
              >
                  <h1 style={{
                    width:"50%",
                    marginLeft:"auto",
                    marginRight:"auto",
                    textAlign:"center",
                    color:"#FE6B8B"
                  }}>
                    Update User
                  </h1>
                  <TextField 
                    style={{
                      marginBottom:"10px",
                      textJustify:"auto",
                      width:"50%",
                      marginLeft:"auto",
                      marginRight:"auto"
                    }}
                    label="Name" 
                    variant="outlined" 
                />
                <TextField 
                    label="Email"
                    style={{
                      marginBottom:"10px",
                      width:"50%",
                      marginLeft:"auto",
                      marginRight:"auto"
                    }} 
                    variant="outlined" 
                />
                <div 
                  style={{
                    display:"flex",
                    flexDirection:"row",
                    justifyContent:"center"
                  }}
                >
                  <Button
                    classes={{
                        root:classes.button
                      }}
                    style={{
                      width:"25%",
                    }}
                  >
                    Close
                  </Button>
                  <Button
                    classes={{
                        root:classes.button
                      }}
                    style={{
                      width:"25%",
                    }}
                  >
                    Update User
                  </Button>
                </div>
              </Dialog>
            </Grid>
          )}
        </div>
    )
}

export default List
