import React,{useState,useEffect} from 'react'
import Tooltip from '@mui/material/Tooltip';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import { makeStyles } from '@mui/styles';
import { useQuery } from "@apollo/client";
import { LOAD_USERS } from './GraphQL/Queries';

const useStyles = makeStyles({
  root: {
    "&:hover":{
      cursor:"pointer"
    }
  },
});

const List = ({style}) => {
    const classes = useStyles();
    const { data } = useQuery(LOAD_USERS);
    const [users, setUsers] = useState([]);
    useEffect(() => {
        if (data) {
          setUsers(data.getAllUsers);
        }
      }, [data]);
    return (
        <div style={style}>
          <div style={{
                display:"flex",
                flexDirection:"column",
                alignItems:"center"
              }}
            >
              <h1 
                style={{
                  color:"#FE6B8B"
                }}>
                Showing list of users detail 
              </h1>
              {users.map((val,index) => {
                  return (
                    <div 
                      style={{
                        display:"flex",
                        flexDirection:"row"
                      }}
                    >
                      <h4> 
                        <span 
                          style={{
                            color:"#FE6B8B"
                          }}>
                          {index}. &nbsp;   
                        </span> 
                          {val.firstName}
                      </h4>
                      <div style={{margin:"auto 30px", display:"flex",flexDirection:"row"}}>
                          <Tooltip title="Update">
                            <div className={classes.root} onClick={()=>console.log("edit")}>
                                <EditIcon/>
                            </div>
                          </Tooltip>
                          <Tooltip title="Delete">
                            <div className={classes.root} onClick={()=>console.log("delete")}>
                                <DeleteIcon/>
                            </div>
                          </Tooltip>
                      </div>
                    </div>
                  )
              })}
          </div>
        </div>
    )
}

export default List
