import { gql } from "@apollo/client";

export const CREATE_USER_MUTATION = gql`
  mutation createUser(
    $name: String!
    $email: String!
  ) {
    createUser(
      name: $firstName
      email: $lastName
    ) {
      name
      email
    }
  }
`;
